// based off https://medium.com/@pdx.lucasm/canvas-with-react-js-32e133c05258
import React, { useState, useRef, useEffect, Component } from 'react';
import { Sampler } from "tone";
import A1 from "./audio/ding-1.wav";
import Shaker from "./audio/shaker-1.wav";

const Canvas = props => {

  const canvasRef = useRef(null)

  // TODO: move out of component, should be passed as a prop
  const draw = ctx => {
    ctx.fillStyle = '#000000'
    ctx.beginPath()
    ctx.arc(50, 100, 20, 0, 2*Math.PI)
    ctx.fill()
  }

  useEffect(() => {

    const canvas = canvasRef.current
    const context = canvas.getContext('2d')

    //Our draw come here
    draw(context)
  }, [draw])

  //Set up tone sampler
  const [isLoaded, setLoaded] = useState(false);
  const sampler = useRef(null);

  useEffect(() => {
    sampler.current = new Sampler(
      { A1, Kick },
      {
        onload: () => {
          setLoaded(true);
        }
      }
    ).toMaster();
  }, []);

  //Function to make a tone
  function playTone(sample) {
    if (isLoaded){
      sampler.current.triggerAttack(sample);
    }
  }

  //Timed function that calls the tone generation regularly
  setInterval(playTone("A1"), 3000);

  return <canvas ref={canvasRef} {...props}/>
}

export default Canvas
